import bgVideo from "../assets/videoplayback2.mp4"
import searchIcon from "../assets/search.ico"
import  { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, useNavigate } from "react-router-dom";
const Axios = require('axios');
const Location = () => {
const [movies, setMovies] = useState([]);
const navigate = useNavigate();
    const searchMovies = () => {
        Axios.get("http://localhost:5001/movies")
        .then((response)=>{
            console.log(response.data)
            setMovies(response.data)
        });
    }
    useEffect(() => {  
        searchMovies()
    }, []);

    const getSchedule = (id) =>{
        Axios.get(`http://localhost:5001/${id}/schedule`)
        .then((response)=>{
            localStorage.setItem("schedule",JSON.stringify(response.data));
            navigate("/schedule");
        });
    }
    return (
        <div className="videoPreview">
            <div className="overlay"></div>
            <video src={bgVideo} autoPlay loop></video>
            <div className="menuPanel">
                <h1 className="specialCase">Movie Booking System</h1>
                <input className="searchBar" placeholder="Enter search phrase" type="text"></input>
                <button>Login/Signup</button>
            </div>
            <div className="headings">
                <h3 className="specialCase">Choose your Movie</h3>
            </div>

            <div className="catalog">
                {
                movies?.length > 0
                    ? (
                        <div className="container">

                            { movies.map((movie) => (

                                <div className="movie" id={movie.id}   onClick = {() => getSchedule(movie.id)}>
                                    <div>
                                        <p>{movie.title}</p>
                                    </div>
                                    <div>
                                        <img src={movie.imagepath} alt={movie.title} />
                                    </div>
                                    <div>
                                        <span>{movie.title}</span>
                                        <h3>{movie.title}</h3>
                                    </div>
                                </div>
                            ))} 
                        </div>

                    ) : (
                        <div className="empty">
                            <h2>No Movie found</h2>
                            <div>
                                 <img src="./../notFound.png"/>
                             </div> 
                        </div>
                    )
            }
            </div>
        </div>
    );
}
export default Location;
